import { Link } from "react-router-dom";
import Tag from "./Tag";
import { useEffect, useState } from "react";
import { Avatar } from "primereact/avatar";
import AuthorService from "../../services/AuthorService";

export default function PostSmallImg({ postData }) {
	return (
		<div
			className="PostSmallImg"
			style={{
				minWidth: "200px",
				width: "200px",
				borderRadius: "1rem",
				overflow: "hidden",

				border: "1px solid var(--surface-border)",
			}}
		>
			<Link
				style={{
					textDecoration: "none",
					color: "inherit",
				}}
				to={{
					pathname: "/post/" + postData.id,
				}}
			>
				<img
					src={"https://picsum.photos/seed/" + postData.id + "/1920/1080"}
					alt="zdjÄ™cie"
					style={{
						objectFit: "cover",
						verticalAlign: "top",
						height: "280px",
						width: "200px",
					}}
				/>
				<div
					style={{
						padding: ".5rem",
						fontSize: ".75rem",
						maxHeight: "2.5rem", // '2rem
						display: "-webkit-box",
						WebkitBoxOrient: "vertical",
						WebkitLineClamp: 2,
						height: "2.5rem",
						overflow: "hidden",
						textOverflow: "ellipsis",
						fontWeight: "bold",
					}}
				>
					{postData.title}
				</div>
			</Link>
			<Link
				style={{
					padding: ".5rem",
					fontSize: ".75rem",
					display: "flex",
					textDecoration: "none",
					color: "inherit",
				}}
				to={{
					pathname: "/author/" + postData.authorId,
				}}
			>
				<Avatar icon="pi pi-user" size="small" shape="circle" />
				<div
					style={{
						display: "flex",
						flexDirection: "column	",
						marginLeft: ".5rem",
					}}
				>
					<div style={{ fontWeight: "bold" }}>{postData.authorName}</div>
					{new Date(postData.createdAt).toLocaleDateString("en-EN", {
						year: "numeric",
						month: "long",
						day: "numeric",
					})}
				</div>
			</Link>
		</div>
	);
}
